mapper.insert:

INSERT INTO CURRENT_ASSET_DETAIL
( ASSET_CODE
, ACQUISITION_COST
, AMORTIZATION_WAY
, DEPARTMENT
, DEPRECIATION
, BOOK_VALUE
, USEFUL_LIFE
, AMORTIZATION_FINAL_YEAR
, ACQUISITION_QUANTITY
, CHANGE_QUANTITY
, REMAIN_QUANTITY
, AMORTIZATION_RATE
, MONTH
, NORMAL_AMORTIZATION
, ACCUMULATED_AMORTIZATION
, BOOK_VALUE_END )
VALUES
( #{assetCode}
, #{acquisitionCost}
, #{amortizationWay}
, #{department}
, #{depreciation}
, #{bookValue}
, #{usefulLife}
, SUBSTR(#{amortizationFinalYear}, 1, 4)
, #{acquisitionQuantity}
, #{changeQuantity}
, #{remainQuantity}
, RTRIM(TO_CHAR(#{amortizationRate}, 'FM9990D99'), '.')
, #{month}
, #{normalAmortization}
, #{accumulatedAmortization}
, #{bookValueEnd} )

mapper.update:

UPDATE CURRENT_ASSET_DETAIL
SET DEPARTMENT = #{department}
  , USEFUL_LIFE = #{usefulLife}
  , AMORTIZATION_FINAL_YEAR = SUBSTR(#{amortizationFinalYear}, 1, 4)
  , CHANGE_QUANTITY = #{changeQuantity}
  , REMAIN_QUANTITY = #{remainQuantity}
  , AMORTIZATION_RATE = RTRIM(TO_CHAR(#{amortizationRate}, 'FM9990D99'), '.')
  , NORMAL_AMORTIZATION = #{normalAmortization}
  , ACCUMULATED_AMORTIZATION = #{accumulatedAmortization}
  , BOOK_VALUE_END = #{bookValueEnd}
WHERE ASSET_CODE = #{assetCode}
